import React from 'react';
import axios from 'axios'
import {
  BrowserRouter as Router,
  useParams
} from "react-router-dom";

class UserList extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            users: []
        }
        axios
            .get('http://localhost:8000/api/v1/users', {
                headers: {
                    Authorization: 'jwt eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoxLCJ1c2VybmFtZSI6ImFkbWluIiwiZXhwIjoxNjA0MDQ1MzEyLCJlbWFpbCI6InF3ZXJAZ21haWwuY29tIiwib3JpZ19pYXQiOjE2MDM0NDA1MTJ9.HMExjqQe4M8zeqpS2TTi_59IkuvGPfVTd1DfY1rq43w'
                }
            })
            .catch(err => console.log(err))
            .then((res,) => this.setState(
                {user: res.data}
            ))
    }

    render() {
        const content = this.state.users.map((user) =>
            <div key={user.id}>
                <p>Username: {user.username}</p>
                <p>Full name: {user.first_name} {user.last_name} </p>
                <p>Email: {user.email} </p>
            </div>

        )
        return (
            <div>
                {content}
            </div>
        )
    }
}

class SingleUser extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            id: useParams()
        }
         .get(`http://localhost:8000/api/v1/users/${this.state.id}`, {
                headers: {
                    Authorization: 'jwt eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoxLCJ1c2VybmFtZSI6ImFkbWluIiwiZXhwIjoxNjA0MDQ1MzEyLCJlbWFpbCI6InF3ZXJAZ21haWwuY29tIiwib3JpZ19pYXQiOjE2MDM0NDA1MTJ9.HMExjqQe4M8zeqpS2TTi_59IkuvGPfVTd1DfY1rq43w'
                }
            })
            .catch(err => console.log(err))
            .then((res,) => this.setState(
                {users: res.data}
            ))
    }

    render() {
        return (
            <div key={this.state.user.id}>
                <p>Username: {this.state.user.username}</p>
                <p>Full name: {this.state.user.first_name} {this.state.user.last_name} </p>
                <p>Email: {this.state.user.email} </p>
            </div>
        )
    }
}

export default UserList
