import React from 'react';
import axios from 'axios'

class UserList extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            user: ''
        }

        if ("id" in props.match){
            axios.get(`http://localhost:8000/api/v1/users/${props.match.id}/`, {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .catch(err => console.log(err))
            .then((res,) => this.setState(
                {user: res.data}
            ))
        }
        else {
            console.log(props.match)
            axios
                .get('http://localhost:8000/api/v1/users/', {
                    headers: {
                        Authorization: localStorage.getItem('trading_api_token')
                    }
                })
                .catch(err => console.log(err))
                .then((res,) => this.setState(
                    {users: res.data}
                ))
        }
    }

    render() {
        const content = this.state.users.map((user) =>
            <User user={user} />
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}

class SingleUser extends React.Component{

}

class User extends React.Component{
    render() {
        return (
            <div key={this.props.user.id}>
                <p>Username: {this.props.user.username}</p>
                <p>Full name: {this.props.user.first_name} {this.props.user.last_name} </p>
                <p>Email: {this.props.user.email} </p>
            </div>
        )
    }
}

export  {
    UserList,
    SingleUser
}
