class Watchlist extends React.Component{
    render(){
        axios.get('http://localhost/api/v1/watchlist/', {
            headers: {
                Authorization: localStorage.getItem('trading_api_token')
            }
        })
        .then(watchlist => {
            const content = watchlist.data.map((item) =>
                <div key={item.id}>
                    <p>{item.item.code}</p>
                    <p>{item.item.name}</p>
                    <p>{item.item.logo}</p>
                    <p>{item.item.details}</p>
                    <p>{item.item.demanded}</p>
                    <p>Currency: {item.item.currency.name}</p>
                </div>
        )
            return content
        })
        .then(content => {
        return (
            <div>
                {content}
            </div>
        )})
    }
}

export {
    Watchlist
}
