import React from "react";
import axios from "axios"

let TRANSACTION_TYPE = {1: "purchase", 2: "sale"}

class OffersView extends React.Component{
    render() {
        const content = this.props.offers.map(item =>
            <div key={item.id}>
                <p>{item.item.code}</p>
                <p>{item.item.name}</p>
                <p>{item.item.logo}</p>
                <p>{item.item.details}</p>
                <p>Transaction type: {TRANSACTION_TYPE[item.transaction_type]}</p>
                <p>User: {item.user.username}</p>
                <p>Currency: {item.item.currency.name}</p>
                <p>Entry quantity: {item.entry_quantity}</p>
                <p>Quantity: {item.quantity}</p>
                <p>Reserved quantity: {item.reserved_quantity}</p>
                <p>Price: {item.price}</p>
            </div>
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}

class CreateOfferForm extends React.Component{
    constructor(props) {
        super(props);
        this.state ={
            item: '',
            entry_quantity: 1,
            order_type: 1,
            transaction_type: 1,
            price: 0,
        }
        this.handleInputChange = this.handleInputChange.bind(this)
    }

    handleInputChange(event) {
        const target = event.target;
        const value = event.value;
        const name = target.name;
        this.setState({
            [name]: value
        });
        this.makeItemOptions()
    }

    handleSubmit(event) {
        axios.post('http://localhost:8000/api/v1/offers/',
            this.state,
            {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            }
        )
        .then(response => console.log(response))
        .then(event.preventDefault())
    }

    makeItemOptions() {
        if (this.state.transaction_type === 1) {
                this.setState({itemOptions: this.props.items.map(item =>
                    <option value={item.id}>{item.name}</option>
                )})
            }
            else {
                this.setState({itemOptions: this.props.inventory.map(item =>
                    <option value={item.item.id}>{item.item.name}</option>
                )})
            }
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Item:
                    <select key={1} name="item" value={this.state.item} onChange={this.handleInputChange}>
                        {this.state.itemOptions}
                    </select>
                </label>
                <label>
                    Quantity:
                    <input key={2} name={"entry_quantity"} type="number" value={this.state.entry_quantity} onChange={this.handleInputChange}/>
                </label>
                <label>
                    Order type:
                    <select key={3} name={"order_type"} value={this.state.order_type} onChange={this.handleInputChange}>
                        <option value={1}>Market</option>
                        <option value={2}>Something else</option>
                    </select>
                </label>
                <label>
                    Transaction type:
                    <select key={4} name={"transaction_type"} value={this.state.order_type} onChange={this.handleInputChange}>
                        <option value={1}>Purchase</option>
                        <option value={2}>Sell</option>
                    </select>
                </label>
                <label>
                    Price:
                    <input key={5} name={"price"} type="number" value={this.state.price} onChange={this.handleInputChange}/>
                </label>
            </form>
        )
    }
}

class Offer extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            offers: [],
            items: [],
            inventory: []
        }
        axios
            .get('http://localhost:8000/api/v1/offers/', {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .then(response => this.setState({offers: response.data}))
            .catch(err => console.log(err))
        axios
            .get('http://localhost:8000/api/v1/items/', {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .then(response => this.setState({items: response.data}))
            .catch(err => console.log(err))
        axios
            .get('http://localhost:8000/api/v1/inventory/', {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .then(response => this.setState({inventory: response.data}))
            .catch(err => console.log(err))
    }
    render() {
        return (
            <div>
                <OffersView offers={this.state.offers}/>
                <CreateOfferForm items={this.state.items} inventory={this.state.inventory}/>
            </div>
        )
    }
}

export default Offer
