import React from 'react';
import axios from 'axios'
import {
  useParams
} from "react-router-dom";

class UserList extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            users: []
        }
        axios
            .get('http://localhost:8000/api/v1/users/', {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .catch(err => console.log(err))
            .then((res,) => this.setState(
                {users: res.data}
            ))
    }

    render() {
        const content = this.state.users.map((user) =>
            <div key={user.id}>
                <p>Username: {user.username}</p>
                <p>Full name: {user.first_name} {user.last_name} </p>
                <p>Email: {user.email} </p>
            </div>
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}

class SingleUser extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            user: ''
        }
        const {params} = props.match
        axios.get(`http://localhost:8000/api/v1/users/${params.id}/`, {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .catch(err => console.log(err))
            .then((res,) => this.setState(
                {user: res.data}
            ))
    }

    render() {
        return (
            <div key={this.state.user.id}>
                <p>Username: {this.state.user.username}</p>
                <p>Full name: {this.state.user.first_name} {this.state.user.last_name} </p>
                <p>Email: {this.state.user.email} </p>
            </div>
        )
    }
}

export  {
    UserList,
    SingleUser
}
