import React from "react";
import axios from "axios"

class AddWatchlist extends React.Component{
    addToWatchlist(ItemId){
        axios
            .post('http://localhost:8000/api/v1/watchlist/', {
            item: ItemId
        },
                {
                    headers: {
                        Authorization: localStorage.getItem('trading_api_token')
                    }
                }
        )
    }

    render() {
        const itemsNotInWatchlist = this.props.items.filter(item => {
            this.props.watchlist.find(watchlist => watchlist.item.id == item.id)
            }
        )
        const content = this.props.items.map((item) =>
            <div key={item.id}>
                <p>{item.code}</p>
                <p>{item.name}</p>
                <p>{item.logo}</p>
                <p>{item.details}</p>
                <p>Currency: {item.item.currency.name}</p>
                <button onClick={() => this.addToWatchlist(item.id)}>Add to watchlist</button>
            </div>
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}


class UsersWatchlist extends React.Component{
    render(){
        const content = this.props.watchlist.map((item) =>
            <div key={item.id}>
                <p>{item.item.code}</p>
                <p>{item.item.name}</p>
                <p>{item.item.logo}</p>
                <p>{item.item.details}</p>
                <p>{item.item.demanded}</p>
                <p>Currency: {item.item.currency.name}</p>
            </div>
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}


class Watchlist extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            watchlist: axios
                .get('http://localhost:8000/api/v1/watchlist/', {
                    headers: {
                        Authorization: localStorage.getItem('trading_api_token')
                    }
                })
                .then(response => response.data)
                .catch(err => {
                    console.log(err)
                    return []
                }),
            items: axios
                .get('http://localhost:8000/api/v1/items/', {
                    headers: {
                        Authorization: localStorage.getItem('trading_api_token')
                    }
                })
                .then(response => response.data)
                .catch(err => {
                    console.log(err)
                    return []
                }),
            isAdding : false
        }
    }

    render() {
        if (this.state.isAdding){
            return <AddWatchlist watchlist={this.state.watchlist} items={this.state.items}/>
        }
        else{
            return <UsersWatchlist watchlist={this.state.watchlist} />
        }
    }
}


export {
    UsersWatchlist,
    AddWatchlist
}
