import React from "react";
import axios from "axios"

class AddWatchlist extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            items: []
        }
         axios
            .get('http://localhost:8000/api/v1/items/', {
            headers: {
                Authorization: localStorage.getItem('trading_api_token')
            }
        })
            .then(response => this.setState(
                {
                    items: response.data
                }
            ))
            .catch(err => console.log(err))
    }

    addToWatchlist(ItemId){
        axios
            .post('http://localhost:8000/api/v1/watchlist/', {
            item: ItemId
        },
                {
                    headers: {
                        Authorization: localStorage.getItem('trading_api_token')
                    }
                }
        )

    }


}

class Watchlist extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            watchlist: []
        }
        axios
            .get('http://localhost:8000/api/v1/watchlist/', {
            headers: {
                Authorization: localStorage.getItem('trading_api_token')
            }
        })
            .then(response => this.setState(
                {
                    watchlist: response.data
                }
            ))
            .catch(err => console.log(err))
    }

    render(){
        const content = this.state.watchlist.map((item) =>
            <div key={item.id}>
                <p>{item.item.code}</p>
                <p>{item.item.name}</p>
                <p>{item.item.logo}</p>
                <p>{item.item.details}</p>
                <p>{item.item.demanded}</p>
                <p>Currency: {item.item.currency.name}</p>
            </div>
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}

export {
    Watchlist
}
