import React from "react";
import axios from "axios"

let TRANSACTION_TYPE = {1: "purchase", 2: "sale"}

class OffersView extends React.Component{
    render() {
        const content = this.props.offers.map(item =>
            <div key={item.id}>
                <p>{item.item.code}</p>
                <p>{item.item.name}</p>
                <p>{item.item.logo}</p>
                <p>{item.item.details}</p>
                <p>Transaction type: {TRANSACTION_TYPE[item.transaction_type]}</p>
                <p>User: {item.user.username}</p>
                <p>Currency: {item.item.currency.name}</p>
                <p>Entry quantity: {item.entry_quantity}</p>
                <p>Quantity: {item.quantity}</p>
                <p>Reserved quantity: {item.reserved_quantity}</p>
                <p>Price: {item.price}</p>
            </div>
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}

class CreateOfferForm extends React.Component{
    constructor(props) {
        super(props);
        this.state ={
            item: '',
            entry_quantity: 1,
            order_type: 1,
            transaction_type: 1,
            price: 0
        }
        this.handleItemChange = this.handleItemChange.bind(this)
        this.handleQuantityChange = this.handleQuantityChange.bind(this)
        this.handleOrderTypeChange = this.handleOrderTypeChange.bind(this)
        this.handleTransactionTypeChange = this.handleTransactionTypeChange.bind(this)
        this.handlePriceChange = this.handlePriceChange.bind(this)
    }

    handleItemChange(event){
        this.setState({item: event.target.value})
    }

    handleQuantityChange(event){
        this.setState({quantity: event.target.value})
    }

    handleOrderTypeChange(event){
        this.setState({order_type: event.target.value})
    }

    handleTransactionTypeChange(event){
        this.setState({transaction_type: event.target.value})
    }

    handlePriceChange(event){
        this.setState({price: event.target.value})
    }

    handleSubmit(event) {
        axios.post('http://localhost:8000/api/v1/offers/', {
            username: this.state.username,
            password: this.state.password,
            first_name: this.state.first_name,
            second_name: this.state.second_name,
            email: this.state.email
        })
        .then(response => console.log(response))
        .then(event.preventDefault())
    }

    render() {
        const itemOptions = this.props.items.map(item =>
            <option value={item.id}>item.name</option>
        )
        return (
            <form>
                <label>
                    Item:
                    <select value={this.state.item} onChange={this.handleItemChange}>
                        {itemOptions}
                    </select>
                </label>
                <label>
                    Quantity:
                    <input type="number" value={this.state.entry_quantity} onChange={this.handleQuantityChange}/>
                </label>
                <label>
                    Quantity:
                    <input type="number" value={this.state.entry_quantity} onChange={this.handleQuantityChange}/>
                </label>
                <label>
                    Order type:
                    <select value={this.state.order_type} onChange={this.handleOrderTypeChange}>
                        <option value={1}>Market</option>
                    </select>
                </label>
                <label>
                    Transaction type:
                    <select value={this.state.order_type} onChange={this.handleTransactionTypeChange}>
                        <option value={1}>Purchase</option>
                        <option value={2}>Sell</option>
                    </select>
                </label>
                <label>
                    Price:
                    <input type="number" value={this.state.price} onChange={this.handlePriceChange}/>
                </label>
            </form>
        )
    }
}

class Offer extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            offers: [],
            items: [],
            inventory: []
        }
        axios
            .get('http://localhost:8000/api/v1/offers/', {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .then(response => this.setState({offers: response.data}))
            .catch(err => console.log(err))
        axios
            .get('http://localhost:8000/api/v1/items/', {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .then(response => this.setState({items: response.data}))
            .catch(err => console.log(err))
        axios
            .get('http://localhost:8000/api/v1/inventory/', {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .then(response => this.setState({inventory: response.data}))
            .catch(err => console.log(err))
    }
    render() {
        return <OffersView offers={this.state.offers}/>
    }
}

export default Offer
