import React from "react";
import axios from "axios"

class Inventory extends React.Component{
    constructor(props) {
        super(props);
        this.state ={
            inventory: []
        }
        axios
            .get('http://localhost:8000/api/v1/inventory/', {
                headers: {
                    Authorization: localStorage.getItem('trading_api_token')
                }
            })
            .then(response => this.setState({inventory: response.data}))
            .catch(err => console.log(err))
    }
    render() {
        let content = this.state.inventory.map(item =>
            <div key={item.id}>
                <p>{item.item.code}</p>
                <p>{item.item.name}</p>
                <p>{item.item.logo}</p>
                <p>{item.item.details}</p>
                <p>{item.item.demanded}</p>
                <p>Currency: {item.item.currency.name}</p>
                <p>Quantity: {item.quantity}</p>
                <p>Reserved quantity: {item.reserved_quantity}</p>
            </div>
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}

export {Inventory}
