//             <option value={item.id}>item.name</option>
//         )
//     }
// }

class Watchlist extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            watchlist: []
        }
        axios
            .get('http://localhost/api/v1/watchlist/', {
            headers: {
                Authorization: localStorage.getItem('trading_api_token')
            }
        })
            .then(response => this.setState(
                {
                    watchlist: response.data
                }
            ))
    }

    render(){
        const content = this.state.watchlist.data.map((item) =>
            <div key={item.id}>
                <p>{item.item.code}</p>
                <p>{item.item.name}</p>
                <p>{item.item.logo}</p>
                <p>{item.item.details}</p>
                <p>{item.item.demanded}</p>
                <p>Currency: {item.item.currency.name}</p>
            </div>
        )
        return (
            <div>
                {content}
            </div>
        )
    }
}

export {
    Watchlist
}
