function get_request(url){
    fetch(url, {
        method: 'GET',
        headers: {
            Authorization: localStorage.getItem('token')? `jwt ${localStorage.getItem('token')}` : '',
            'Content-Type': 'application/json;charset=utf-8'
        },
    })
    .then(response => response.json())
    .then(data => {
        return data;
    })
    .catch((e) => {
        alert(`Код ошибки ${e}`)
        alert(this.url)
    })
}

